impl Bivector {
    pub fn exp(self) -> ScalarAndBivector {
        let theta = self.norm();
        if theta.abs() < FLOAT_DIVISION_EPSILON {
            1. + self
        } else {
            theta.cos() + (theta.sin() / theta) * self
        }
    }
}

